INSERT INTO `TecCargo_Pages` (`pageId`, `title`, `content`, `whoCanSee`, `useIndent`) VALUES
(2, 'Kurertransport', '<h1><strong><span style="color:#f73700">Kurertransport</span></strong></h1>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">Til forsendelser, der haster. Eller specialtransporter, der kr&aelig;ver en s&aelig;rlig h&aring;ndtering alle dage 24/7.</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">Vi leverer alt fra anbefalede breve, dokumenter, mindre pakker til st&oslash;rre pallegods. Vores Kurertransport</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">henter og bringer hurtigt, sikkert til modtageren og altid til tiden.</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;">&nbsp;</div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">Kurer levering er en fleksibel l&oslash;sning, der im&oslash;dekommer samtlige af vores kunders krav. Har du specielle</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">&oslash;nsker til Express levering, kurer k&oslash;rsel, haste transport og direkte budservice, uanset tid p&aring; dagen eller</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">ugen, s&aring; er vi sikre p&aring;, vi kan tilpasse os til netop dit behov.</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;">\r\n<hr /></div>\r\n\r\n<h2 style="font-style:normal"><span style="font-size:16px"><strong><span style="color:#254078">Express</span><span style="color:#f73700">GoRush</span>&trade;</strong></span><img alt="" src="http://teccargo.dk/images/logo/goRush.png" style="float:right; height:100px; width:100px" /></h2>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px"><strong>GoRush</strong> levering for dine mest tidskritiske forsendelser &ndash; n&aring;r det haster!</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;">&nbsp;</div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">Har du en forsendelse, der skal leveres nu.... S&aring; tilbyder vi GoRush levering, hvor vi p&aring;begynder opgaven</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">indenfor 30 minutter efter booking og leverer direkte til modtageren.&nbsp;</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal; text-align: right;"><input type="button" value="showPage(''../kurer_price/gorush/notemplate/'');|Se Pris| buttonsColorBlue" />&nbsp; &nbsp; &nbsp;</div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;">\r\n<hr /></div>\r\n\r\n<h2 style="font-style:normal"><span style="font-size:16px"><strong><span style="color:#254078">Express</span><span style="color:#f73700">GoFlex</span>&trade;</strong>&nbsp;<img alt="" src="http://teccargo.dk/images/logo/goFlex.png" style="float:right; height:100px; width:110px" /></span></h2>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px"><strong>GoFlex</strong> ideel til skr&aelig;ddersyet levering inden for et bestemt tidspunkt.&nbsp;</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;">&nbsp;</div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">GoFlex er til dig, der &oslash;nsker at f&aring; leveret din forsendelse dato- og tidsbestemt. Et billigere alternativ til</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">vores GoRush og GoVIP leveringer.</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal; text-align: right;"><span style="font-size:16px"><input type="button" value="showPage(''../kurer_price/goflex/notemplate/'');|Se Pris| buttonsColorBlue" />&nbsp; &nbsp; &nbsp;</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;">\r\n<hr /></div>\r\n\r\n<h2 style="font-style:normal"><span style="font-size:16px"><strong><span style="color:#254078">Express</span><span style="color:#f73700">GoVIP</span>&trade;<img alt="" src="http://teccargo.dk/images/logo/goVip.png" style="float:right; height:100px; width:111px" /></strong></span></h2>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;">&nbsp;</div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px"><strong>GoVIP </strong>er vores eksklusive service, der benyttes hvis opgaven kr&aelig;ver en meget speciel h&aring;ndtering.</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">Vi tilbyder at sende en medarbejder, der afhenter forsendelsen direkte hos Dem, og leverer personligt til</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">Deres kunder i hele verden.&nbsp;</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;">&nbsp;</div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">Dette er den hurtigste og sikreste service, der kan benyttes til opgaver som licitationer, kontrakter, vigtige</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">reservedele eller pr&aelig;sentationer.</span></div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;">&nbsp;</div>\r\n\r\n<div style="font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;"><span style="font-size:16px">Kort sagt opgaver der ikke m&aring; sl&aring; fejl, og som skal overholde helt specielle deadlines eller h&aring;ndteringer.</span></div>', 0, 1),
(4, 'Pakketransport', '<div>\r\n<h1><span style="color:#f73700"><strong>Pakketransport</strong></span></h1>\r\n\r\n<div><span style="font-size:16px">N&aring;r du benytter TECcargo&acute;s pakketransport kan du v&aelig;re sikker p&aring;, at pakken n&aring;r hurtigt og sikkert frem.<br />\r\nVi h&aring;ndterer og distribuerer dagligt pakkeforsendelser p&aring; vegne af vore kunder til danske<br />\r\nerhvervsvirksomheder, detailbutikker og private kunder.<br />\r\n<br />\r\nVi d&aelig;kker hele Danmark inden for 24 timer. Det g&oslash;r os til &eacute;n af de sikreste og hurtigste pakkedistribut&oslash;rer<br />\r\ni Danmark.</span><br />\r\n<br />\r\n<span style="font-size:16px">Med TECcargo&acute;s prioriteret behandling under hele processen sikrer vi &oslash;get tryghed.<br />\r\nVi tager fuldt ansvar for dine ting, imens de er i vores varet&aelig;gt.</span></div>\r\n\r\n<div>\r\n<hr /></div>\r\n</div>\r\n\r\n<div>\r\n<p><strong><span style="font-size:16px"><span style="color:#254078">Priority</span><span style="color:#f73700">GoPlus</span>&trade;</span></strong><img src="http://teccargo.dk/images/logo/goPlus.png" style="float:right; height:100px; width:95px" /></p>\r\n\r\n<p><span style="font-size:16px"><strong>GoPlus</strong>&nbsp;er det ideelle valg til forsendelser der skal v&aelig;re fremme n&aelig;ste arbejdsdag, inden kl. 10.00.<br />\r\n<br />\r\nVi holder selvf&oslash;lgelig l&oslash;bende status over dine forsendelser og vil til enhver tid kunne oplyse dig pr&aelig;cist,<br />\r\nhvor dine forsendelser befinder sig.</span>&nbsp;</p>\r\n\r\n<p style="text-align:right"><span style="font-size:16px"><input type="button" value="showPage(''../pakke_price/goplus/notemplate/'');|Se Pris| buttonsColorBlue" /></span>&nbsp; &nbsp;</p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n</div>\r\n\r\n<div>\r\n<p><img src="http://teccargo.dk/images/logo/goGreen.png" style="float:right; height:100px; width:96px" /><strong><span style="font-size:16px"><span style="color:#254078">Economy</span><span style="color:#5AC440">GoGreen</span>&trade;</span></strong></p>\r\n\r\n<div><span style="font-size:16px"><strong>GoGreen</strong>&nbsp;en &oslash;konomisk og milj&oslash;bevidst service til mindre presserende leveringer.<br />\r\nDet ideelle valg n&aring;r &oslash;konomi og milj&oslash; skal balancere.<br />\r\n<br />\r\nVi tilbyder vores kunder at medvirke til at reducere Co2-udledningen.</span></div>\r\n\r\n<p><span style="font-size:16px">GoGreen levering skal prim&aelig;rt bidrage til at neds&aelig;tte spildte kilometer, og dermed hj&aelig;lpe p&aring;<br />\r\nmindre&nbsp;Co2 udledning. Sidst men ikke mindst, er dette ogs&aring; den billigste leverancetype</span></p>\r\n\r\n<p style="text-align:right"><span style="font-size:16px"><input type="button" value="showPage(''../pakke_price/gogreen/notemplate/'');|Se Pris| buttonsColorGreen" />&nbsp; &nbsp;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n', 0, 1),
(5, 'Godstransport', '<div>\r\n<h1><strong><span style="color:#f73700">Godstransport</span></strong></h1>\r\n\r\n<div><span style="font-size:16px">Med vores mange &aring;rs erfaring inden for transportbranchen kan vi tilbyde vores kunder en enest&aring;ende<br />\r\nl&oslash;sning i forbindelse med godstransport og andre transportopgaver.<br />\r\n<br />\r\nVi h&aring;ndterer stykgods og pallegods b&aring;de enkeltvis, i sm&aring; portioner eller i st&oslash;rre partier til en &oslash;konomisk<br />\r\nf&oslash;rste-klasses d&oslash;r-til-d&oslash;r service med attraktive transittider og til en overkommelig konkurrencedygtig<br />\r\npris.<br />\r\n<br />\r\nVi h&aring;ndterer altid vores godstransport med st&oslash;rste omhu, som var det vores eget.<br />\r\nVi leverer altid kvalitetsarbejde til den aftalte tid, og s&oslash;rger for at godset bliver transporteret p&aring; den<br />\r\nmest effektive og sikre m&aring;de.<br />\r\n<br />\r\nHos TECcargo er vores medarbejdere dygtige, engagerede og videref&oslash;rende af vores v&aelig;rdier med<br />\r\ngodstransport i ethvert stykke arbejde.</span></div>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n</div>\r\n\r\n<div>\r\n<div><img src="http://teccargo.dk/images/logo/goFull.png" style="float:right; height:100px; width:100px" /></div>\r\n\r\n<h3><strong><span style="font-size:16px"><span style="color:#254078">Cargo</span><span style="color:#f73700">GoFull</span>&trade;</span></strong></h3>\r\n\r\n<h4><span style="font-size:16px">Cargo service</span></h4>\r\n\r\n<ul>\r\n	<li><span style="font-size:16px">SameDay Delivery</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:16px">Med en&nbsp;GoFull&nbsp;transport fylder vi hele fragtbilen med varer fra et opsamlingssted.<br />\r\nTransport af GoFull er altid direkte fra afsender til modtager, uden stop undervejs.</span></p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n</div>\r\n\r\n<div>\r\n<div><img src="http://teccargo.dk/images/logo/goPart.png" style="float:right; height:100px; width:97px" /><strong><span style="font-size:16px"><span style="color:#254078">Cargo</span><span style="color:#f73700">GoPart</span>&trade;</span></strong></div>\r\n\r\n<div><span style="font-size:16px">Cargo service</span></div>\r\n\r\n<ul>\r\n	<li>\r\n	<div><span style="font-size:16px">NextDay Delivery</span></div>\r\n	</li>\r\n</ul>\r\n\r\n<div><span style="font-size:16px">Med en&nbsp;GoPart&nbsp;transport betaler du kun for den plads du fylder p&aring; fragtbilen.<br />\r\nTransporten opsamler og leverer varer fra flere steder p&aring; samme rute, og der kan derfor forekomme<br />\r\noml&aelig;sning og terminal stop.</span></div>\r\n\r\n<div style="text-align: right;"><span style="font-size:16px"><input type="button" value="showPage(''../gods_price/0/notemplate/'');|Prisliste| buttonsColorBlue" /></span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n\r\n<div style="text-align: right;"><span style="font-size:16px"><input type="button" value="showPage(''../gods_price/1/notemplate/'');|Takst Zoner| buttonsColorBlue" /></span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n\r\n<div style="text-align: right;"><span style="font-size:16px"><input type="button" value="showPage(''../gods_price/2/notemplate/'');|Beregning af ladmeter| buttonsColorBlue" /></span></div>\r\n</div>\r\n', 0, 1),
(6, 'Lagerhotel', '<h1><strong><span style="color:#f73700">Lagerhotel</span></strong></h1>\r\n\r\n<p><span style="font-size:16px">En lagerhotelaftale med TECcargo kan bl.a. indeb&aelig;re opbevaring, handling, lagerstyring,<br />\r\nordreekspedition, pluk, m&aelig;rkning og forsendelse, og p&aring; intet tidspunkt beh&oslash;ver du at v&aelig;re til stede.<br />\r\nVi har mulighed for langtids- eller dag-til-dag opbevaring, h&aring;ndtering og videredistribution af de fleste<br />\r\ngodstyper. Vores medarbejdere har stor erfaring i logistikh&aring;ndtering, og vi sikrer dig og din virksomhed en<br />\r\nprofessionel l&oslash;sning.<br />\r\nVores&nbsp;<strong>WHS Logistics</strong>&nbsp;giver dig mulighed for at reducere dine samlede logistikomkostninger gennem<br />\r\neffektive l&oslash;sninger med kvalitetsgaranti.</span></p>\r\n\r\n<hr />\r\n<div><img src="http://teccargo.dk/images/logo/lagerhotel.png" style="float:right; height:100px; width:99px" /></div>\r\n\r\n<h2><strong><span style="color:#254078">WHS</span><span style="color:#f73700">Logistics</span>&trade;</strong></h2>\r\n\r\n<p><span style="font-size:16px">Vi tilbyder lagerhotel, tredjepartslogistik (3.PL) til konkurrencedygtige priser p&aring; lageropbevaring,<br />\r\nfor&aelig;dlingsservices samt distribution til b&aring;de virksomheder og privatpersoner.</span></p>\r\n\r\n<p><span style="font-size:16px"><strong>Stockholding:</strong><br />\r\n&bull; Muligheden for butikken (og evt leverand&oslash;r) for at holde lager p&aring; konsolideringsterminalen.<br />\r\n<br />\r\n<strong>Pre‐retail aktiviteter:</strong><br />\r\n&bull; Varemodtagelse, indgangskontrol, prism&aelig;rkning, tyverisikring.<br />\r\n<br />\r\n<strong>Varebestilling/lagerstyring:</strong><br />\r\n&bull; Vareregistreringer, varebestillinger.<br />\r\n<br />\r\n<strong>Returlogistik:</strong><br />\r\n&bull; Af returemballager, af post, af returvarer.<br />\r\n<br />\r\n<strong>Butik:</strong><br />\r\n&bull; Eksempelvis udpakning af varer, opfyldning p&aring; hylder<br />\r\n&bull; Hjemmeleveringer / facilitering af e‐handel (Pick &amp; Pack)</span></p>\r\n', 0, 1),
(7, 'Montage', '<div>\r\n<h1><span style="color:#f73700"><strong>Montage</strong></span></h1>\r\n\r\n<p><span style="font-size:16px">En messe eller udstilling venter forude, og du vil naturligvis gerne komme godt fra start.<br />\r\nHos TECcargo s&oslash;rger vi for, at din stand st&aring;r parat f&oslash;r udstillingen &aring;bner.</span></p>\r\n\r\n<hr />\r\n<h2><strong><span style="color:#254078">Tech</span><span style="color:#f73700">Montage</span>&trade;</strong></h2>\r\n\r\n<p><span style="font-size:16px">TechMontage er vores Tekniske logistik service som l&oslash;ser alle faser i forbindelse med design og opbygning<br />\r\naf en messe- eller udstillingsstand.<br />\r\nP&aring; selve udstillingsarealet er det vores logistikmedarbejdere/mont&oslash;rer, der s&oslash;rger for at alle detaljer er<br />\r\np&aring; plads og klar til tiden.<br />\r\nDet er ogs&aring; logistikmedarbejderne/mont&oslash;rerne der overdrager den f&aelig;rdige stand til dig, s&aring; I sammen kan<br />\r\ngennemg&aring; det hele og sikre, at alt er fuldendt, og virker som forventet.<br />\r\nLogistikmedarbejderne/mont&oslash;rerne er parate til at l&oslash;se problemer, hvis der opst&aring;r uventede<br />\r\nudfordringer.&nbsp;</span></p>\r\n\r\n<h4><strong><span style="font-size:16px">Nedtagning- og afslutningsfase</span></strong></h4>\r\n\r\n<p><span style="font-size:16px">Efter endt messe skal der ryddes op og pakkes forsvarligt ned, s&aring; det der skal bruges igen, rent faktisk kan<br />\r\nbruges igen.<br />\r\nLogistikmedarbejdernes/mont&oslash;rernes professionelle indstilling til arbejdet betyder at du opn&aring;r en<br />\r\nyderligere omkostningseffektiv l&oslash;sning.</span></p>\r\n\r\n<h4><strong><span style="font-size:16px">Lager hotel opbevaring</span></strong></h4>\r\n\r\n<p><span style="font-size:16px">Hos TECcargo er vi eksperter i at opbevare, beskytte og genanvende de kostbare elementer en moderne<br />\r\nudstilling er opbygget af.<br />\r\nAlle materialer bliver forsvarligt emballeret og indpakkes for yderligere beskyttelse.</span></p>\r\n</div>\r\n', 0, 1),
(8, 'Kvalitetspolitik', '<div>\r\n<h1><span style="color:#f73700"><strong>Kvalitetspolitik</strong></span></h1>\r\n\r\n<p><span style="font-size:16px">Vores kvalitetspolitik er baseret p&aring; hele forl&oslash;bet fra den f&oslash;rste kontakt med kunden til godset er leveret<br />\r\nkorrekt og til tiden hos modtageren.</span></p>\r\n\r\n<h4><span style="font-size:16px">Kvalitets v&aelig;rdier</span></h4>\r\n\r\n<ul>\r\n	<li><span style="font-size:16px">Sikkerhed</span></li>\r\n	<li><span style="font-size:16px">Punktlighed</span></li>\r\n	<li><span style="font-size:16px">Service</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:16px"><strong>TECcargo&acute;s</strong> kvalitetsstyringssystem er med til sikre og fastholde vore kvalitetsm&aring;l.<br />\r\nT&aelig;t samarbejde og god kommunikation mellem kunder, leverand&oslash;rer og medarbejdere sikrer at vi altid<br />\r\nformidler og leverer den bedste l&oslash;sning til vore kunder.<br />\r\n<br />\r\nVi arbejder l&oslash;bende p&aring; at forbedre kvaliteten og optimere de interne processer, for at udnytte vore<br />\r\nressourcer bedst muligt.</span></p>\r\n\r\n<h4><span style="font-size:16px">F&oslash;rsteklasses service til kunden</span></h4>\r\n\r\n<p><span style="font-size:16px"><strong>TECcargo&acute;s</strong> gode kvalitet starter med et personale som kan levere en f&oslash;rsteklasses service til kunden.<br />\r\nGennem en m&aring;lrettet rekruttering og efteruddannelse kan vi sikre, at vores personale er b&aring;de motiveret<br />\r\nog i stand til at opretholde den h&oslash;je standard for kvaliteten i deres arbejde.</span></p>\r\n\r\n<h4><span style="font-size:16px">H&oslash;j standard og driftsikkerhed</span></h4>\r\n\r\n<p><span style="font-size:16px">TECcargo har udarbejdet et egenkontrolprogram, som sikrer en h&oslash;j standard og driftsikkerhed.<br />\r\nVores medarbejdere er faguddannet Logistik- og Transportoperat&oslash;rer.<br />\r\nMedarbejderne gennemg&aring;r vores interne opl&aelig;ringsprogram og f&aring;r en teoretisk viden om vores<br />\r\nvirksomhed og vores kunders krav til kvalitet, milj&oslash;sikkerhed, k&oslash;re/hviletid, &oslash;konomiskk&oslash;rsel og<br />\r\nmedarbejdernes fremtoning.</span></p>\r\n</div>\r\n', 0, 1),
(9, 'Miljøpolitik', '<div>\r\n<h1><strong><span style="color:#f73700">Milj&oslash;politik</span></strong></h1>\r\n\r\n<h4><span style="font-size:16px">TECcargo &oslash;nsker at blive opfattet som et milj&oslash;bevidst og en respekteret servicevirksomhed og<br />\r\nsamarbejdspartner.</span></h4>\r\n\r\n<ul>\r\n	<li><span style="font-size:16px"><em>Vi tilbyder vores kunder at medvirke til at reducere Co2-udledningen, gennem vores<br />\r\n	<span style="color:#5AC440">GoGreen</span>&nbsp;levering, hvor vi neds&aelig;tter spildte kilometer.</em></span></li>\r\n	<li><span style="font-size:16px"><em>Vi str&aelig;ber efter optimering af vort samlede ressourceforbrug.<br />\r\n	Dette g&oslash;res ved at vi i samarbejde med leverand&oslash;rer, kunder og myndigheder aktivt<br />\r\n	deltager i forbedring af den samlede logistik og de afledte milj&oslash;p&aring;virkninger.</em></span></li>\r\n	<li><span style="font-size:16px"><em>Vi motiverer medarbejdere til altid at fokusere p&aring; milj&oslash;m&aelig;ssige forbedringer.</em></span></li>\r\n	<li><span style="font-size:16px"><em>Vi ser milj&oslash;forbedrende tiltag som langsigtede investeringer.</em></span></li>\r\n	<li><span style="font-size:16px"><em>Vi arrangerer en m&aring;lrettet og synlig indsats i forhold til at forebygge ulykker og skader.<br />\r\n	Gennem l&oslash;bende opdatering og implementering af ny lovgivning p&aring; milj&oslash;omr&aring;det sikrer vi<br />\r\n	milj&oslash;m&aelig;ssigt forsvarligt arbejde.</em></span></li>\r\n	<li><span style="font-size:16px"><em>Vi kommunikerer &aring;bent om virksomhedens milj&oslash;- og arbejdsm&aelig;ssige ansvar.</em></span></li>\r\n	<li><span style="font-size:16px"><em>Vi indg&aring;r samarbejde med vores kunder om at skabe bedre milj&oslash;l&oslash;sninger samt synligg&oslash;re<br />\r\n	indsatsen og de l&oslash;bende forbedringer over for kunder, medarbejdere og samfund.</em></span></li>\r\n</ul>\r\n</div>\r\n', 0, 1),
